import axios from "axios";
import { useEffect, useState } from "react";
import { Button, Form } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import LoginForm from "./LoginForm";

const UserHomePage = () => {
  const [userItems, setUserItems] = useState([]);
  const [currentUser, setCurrentUser] = useState([]);

  useEffect(() => {
    axios
      .get("/inventory")
      .then((response) =>
        console.log(response).catch((error) => console.log(error))
      );
  });

  useEffect(() => {
    const user = LoginForm.getCurrentUser();
    if (user) {
      setCurrentUser(user);
    }
  }, []);

  useEffect(() => {
    fetch("https://https://localhost:8000/users/")
      .then((response) => response.json())
      .then((data) => data);
  });
  console.log(currentUser);
  const navigate = useNavigate();
  return (
    <div style={{ width: "90%", margin: "auto auto", textAlign: "center" }}>
      <h1>Welcome to your inventory Page</h1>

      {currentUser.map((user) => (
        <h1>{user.id === currentUser.id}</h1>
      ))}

      <Button onClick={() => navigate("/modify")}>Modify</Button>
      <Button onClick={() => navigate("/guest")}>All Items</Button>
    </div>
  );
};

export default UserHomePage;
