import { useState, useEffect } from "react";

const ItemDetails = ({ item }) => {
  const [details, setDetails] = useState();

  useEffect(() => {
    fetch(`http://localhost:8000/inventory/${item.id}`)
      .then((res) => res.json())
      .then((data) => setDetails(data));
  }, []);

  return <div>
    {details.map}
    </div>;
};

export default ItemDetails;
